import React from 'react';

const Course = ({course}) => {
    return ( 
        <div>
            <h1>{course.name}</h1>
            {course.parts.map((part) => 
                <p key={part.id}>{part.name} {part.exercises}</p>
            )}
            <p>total of {course.parts.reduce((sum, part) => sum + part.exercise, 0)}</p>
        </div>
    )
}

export default Course;
